(function() {
  var creator, toaster;

  toaster = null;

  creator = function(Vue) {
    if (toaster == null) {
      toaster = Vue.extend(creator.obj);
      toaster = new toaster().$mount();
    }
    return toaster;
  };

  creator.obj = require('./toaster-component');

  module.exports = creator;

}).call(this);
